import React from "react";
import "./GithubRepoCard.scss";
import { Fade } from "react-reveal";

export default function GithubRepoCard({ project, isDark }) {
  function openUrlInNewTab(url) {
    if (!url) return;
    const win = window.open(url, "_blank");
    win?.focus();
  }

  return (
    <Fade bottom duration={1000} distance="20px">
      <div
        className={isDark ? "dark-card-mode repo-card-div" : "repo-card-div"}
        key={project.id}
      >
        <div className="repo-name-div">
          <p className="repo-name">{project.name}</p>
        </div>
        <p className="repo-description">{project.description}</p>

        <div className="repo-links">
          <button
            className="repo-link-button"
            onClick={() => openUrlInNewTab(project.githubUrl)}
          >
            GitHub
          </button>
          <button
            className="repo-link-button"
            onClick={() => openUrlInNewTab(project.websiteUrl)}
          >
            Website
          </button>
        </div>
      </div>
    </Fade>
  );
}
